import axios from "axios";
import { useContext, useEffect, useReducer } from "react";
import {useParams} from "react-router-dom";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Badge, Button, Card, ListGroup } from "react-bootstrap";
import Rating from "../components/Rating";
import { Helmet } from "react-helmet-async";
import { Store } from "../Store";

const reducer = (state, action) => {
    switch(action.type) {
      case 'FETCH_REQUEST':
        return {...state, loading: true};
      case 'FETCH_SUCCESS':
        return {...state, product: action.payload, loading: false};
      case 'FETCH_FAIL':
        return {...state, loading: false, error: action.payload};
      default:
        return state;
    }
  }

function ProductScreen(){
    const params = useParams();
    const {slug} = params;
    const [{loading, error, product}, dispatch] = useReducer(reducer, {
        product: [],
        loading: true,
        error: '',
      });
      //const [products, setProducts] = useState([]);
      useEffect(() => {
        const fetchData = async () => {
          dispatch({type: 'FETCH_REQUEST'});
          try {
          const result = await axios.get(`http://127.0.0.1:9000/api/products/slug/${slug}`);
          dispatch({type:'FETCH_SUCCESS', payload: result.data});
          } catch(err) {
           dispatch({type: 'FETCH_FAIL', payload: err.message});
    
          }
          //setProducts(result.data);
        }
        fetchData();
      }, [slug]);

      const {state, dispatch: ctxDispatch} = useContext(Store);
      const {cart} = state;
      const addToCartHandler = async () => {
        const existItem = cart.cartItems.find((x) => x._id === product._id);
        const quantity = existItem ? existItem.quantity + 1 : 1;
        const {data} = await axios.get(`http://127.0.0.1:9000/api/products/${product._id}`);
        if (data.countInStock < quantity) {
            window.alert('Sorry, out of stock!');
            return;
        }
        ctxDispatch({
            type:'CART_ADD_ITEM',
            payload: {...product, quantity: 1 },    
        });
      };
    return (
        loading? <div>Loading...</div>
        : error? <div>{error}</div>
        : <div>
            <Row>
                <Col md={6}>
                    <img className="img-large"
                    src={product.image}
                    alt={product.name}>
                    </img>
                </Col>
                <Col md={3}>
                <ListGroup variant="flush">
                    <Helmet>
                        <title>{product.name}</title>
                    </Helmet>
                    <ListGroup.Item>
                        <h1>{product.name}</h1>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Rating
                            rating={product.rating}
                            numReviews={product.numReviews}    
                        ></Rating>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        Price: ${product.price}
                    </ListGroup.Item>
                    <ListGroup.Item>
                        Description: <p>{product.description}</p>
                    </ListGroup.Item>
                </ListGroup>
                </Col>
                <Col md={3}>
                    <Card>
                        <Card.Body>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Price:</Col>
                                        <Col>${product.price}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>STatus:</Col>
                                        <Col>
                                            {product.countInStock>0?
                                            <Badge bd="success">In Stock</Badge>
                                            :<Badge bg="danger">Unavailable</Badge>}
                                        </Col>
                                    </Row>
                                </ListGroup.Item>
                                {product.countInStock > 0 && (
                                    <ListGroup.Item>
                                        <div className="d-grid">
                                            <Button onClick={addToCartHandler}variant="primary">Add to Cart</Button>
                                        </div>
                                    </ListGroup.Item>
                                )}
                            </ListGroup>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </div>
    )
    }
export default ProductScreen;
