import React, { useContext, useEffect, useReducer } from "react";
import MessageBox from "../components/MessageBox";
import { Store } from "../Store";
import { Link, useNavigate, useParams } from "react-router-dom";
import Axios from "axios";
import { Helmet } from "react-helmet-async";
import { Card, Col, ListGroup, Row } from "react-bootstrap";
import { getError } from "../utils";
import {PayPalButtons, usePayPalScriptReducer} from '@paypal/react-paypal-js';

function reducer(state, action) {
    switch(action.type) {
        case 'FETCH_REQUEST':
            return {...state, loading: true, error:''};
        case 'FETCH_SUCCESS':
            return {...state, loading: false, order: action.payload, error:''};
        case 'FETCH_FAIL':
            return {...state, loading: false, error: action.payload};
        case 'PAY_REQUEST':
            return {...state, loadingPay: true};
        case 'PAY_SUCCESS':
            return {...state, loadingPay: false, successPay: true};
        case 'PAY_FAIL':
            return {...state, loadingPay: false};
        case 'PAY_RESET':
            return {...state, loadingPay: false, successPay: false};
        default:
            return state;
    }
}

export default function OrderScreen(){
    const params = useParams();
    const {id: orderId} = params;
    const navigate = useNavigate();
    const {state} = useContext(Store);
    const {userInfo} = state;
    

    const [{loading, error, order, successPay, loadingPay}, dispatch] = useReducer(reducer, {
        loading: true,
        order: {},
        error: '',
        successPay: false,
        loadingPay: false,
    });

    const [{isPending}, paypalDispatch] = usePayPalScriptReducer();

    function createOrder(data, actions) {
        return actions.order
        .create({
            purchase_units: [
                {
                    amount: {value: order.totalPrice*100},
                },
            ],
        })
        .then((orderID) => {
            return orderID;
        });
    }

    function onApprove(data, actions) {
        return actions.order.capture().then (async function(details){
            try {
                dispatch({type: 'PAY_REQUEST'});
                const {data} = await Axios.put(`http://127.0.0.1:9000/api/orders/${order._id}/pay`,
                details,
                {
                    headers: {authorization: `Bearer ${userInfo.token}`},
                }
                );
                dispatch({type: 'PAY_SUCCESS', payload: data});

                alert("Order is Paid!");
            } catch (err) {
                dispatch({type: 'PAY_FAIL', payload: getError(err)});
                alert(getError(err));
            }
        });
    }
    function onError(err) {
        alert(getError(err));
    }

    useEffect(() => {
        const fetchOrder = async () => {
            try {
                dispatch({type: 'FETCH_REQUEST'});
                const {data} = await Axios.get(`http://127.0.0.1:9000/api/orders/${orderId}`, {
                    headers: {authorization: `Bearer ${userInfo.token}`},
                });
                dispatch({type: 'FETCH_SUCCESS', payload: data});
            }catch (err) {
                dispatch({type: 'FETCH_FAIL', payload: getError(err)});
            }
        };
        if (!userInfo) {
            return navigate('/login');
        }
        if (!order._id || successPay || (order._id && order._id !== orderId)
        ){
            fetchOrder();
            if(successPay) {
                dispatch({type: 'PAY_RESET'});
            }
        } else {
            const loadPayPalScript = async () => {
                const {data: clientId} = await Axios.get('http://127.0.0.1:9000/api/keys/paypal',  {
                    headers: {authorization: `Bearer ${userInfo.token}`},
                });
                paypalDispatch({
                    type: 'resetOptions',
                    value: {
                        'client-id': clientId,
                        currency: 'USD',
                    },
                });
                paypalDispatch({tyoe: 'setLoadingStatus', value: 'pending'});
            }
            loadPayPalScript();
        }
    }, [order, userInfo, orderId, navigate, paypalDispatch, successPay]);

    return (
        loading ? (
            <h1>Loading...</h1>
        ) :error ? (
            <MessageBox variant="danger">{error}</MessageBox>
        ) : (
            <div className="pos">
                <Helmet>
                    <title>
                        Order {orderId}
                    </title>
                </Helmet>
                <h1 className="my-3">Order {orderId}</h1>
                <Row>
                    <Col md={8}>
                    <Card className="mb-3">
                            <Card.Body>
                                <Card.Title>Shipping</Card.Title>
                                <Card.Text>
                                    <strong>Name:</strong> {order.shippingAddress.fullName} <br />
                                    <strong>Address:</strong> {order.shippingAddress.address},{order.shippingAddress.city},{order.shippingAddress.postalCode},{order.shippingAddress.country}
                                </Card.Text>
                                {order.isDelivered ? (
                                    <MessageBox vartiant="success">
                                        Delivered at {order.deliveredAt}
                                    </MessageBox>
                                ) : (
                                    <MessageBox variant="danger">Not Delivered</MessageBox>
                                )}
                            </Card.Body>
                        </Card>
                        <Card className="mb-3">
                            <Card.Body>
                                <Card.Title>Items</Card.Title>
                                <ListGroup variant="flush">
                                    {order.orderItems.map((item) => (
                                        <ListGroup.Item key={item._id}>
                                            <Row className="align-items-center">
                                                <Col md={6}>
                                                    <img src={item.image} alt={item.name} className="img-fluid rounded img-thumbnail"></img>
                                                    {' '}
                                                    <Link to={`/product/${item.slug}`}>{item.name}</Link>
                                                </Col>
                                                <Col md={3}>
                                                    <span>{item.quantity}</span>
                                                </Col>
                                                <Col md={3}>${item.price}</Col>
                                            </Row>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            </Card.Body>
                        </Card>
                    </Col>
                    <Col md={4}>
                        <Card className="mb-3">
                            <Card.Body>
                                <Card.Title>Order Summary</Card.Title>
                                <ListGroup variant="flush">
                                <ListGroup.Item>
                                        <Row>
                                            <Col>Items</Col>
                                            <Col>${order.itemsPrice.toFixed(2)}</Col>
                                        </Row>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Shipping</Col>
                                            <Col>${order.shippingPrice.toFixed(2)}</Col>
                                        </Row>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Tax</Col>
                                            <Col>${order.taxPrice.toFixed(2)}</Col>
                                        </Row>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Order Total</Col>
                                            <Col>${order.totalPrice.toFixed(2)}</Col>
                                        </Row>
                                    </ListGroup.Item>
                                    {!order.isPaid && (
                                        <ListGroup.Item>
                                            {isPending ? (<strong>Loading...</strong>)
                                            : (
                                                <div>
                                                    <PayPalButtons 
                                                    createOrder={createOrder}
                                                    onApprove={onApprove}
                                                    onError={onError}
                                                    />

                                                </div>
                                            )}
                                            {loadingPay && <strong>Loading...</strong>}
                                        </ListGroup.Item>
                                    )}
                                </ListGroup>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </div>
        )
    )
}
